import React, { useState } from "react";
import { OpenAIApi } from "openai";

const apiKey = process.env.OPENAI_API_KEY;
// create new configuration for the OpenAI API with the API key
const configuration = new Configuration({
    apiKey: apiKey
});
// create new instance of OPENAIAPI class with configuration
const openai = new OpenAIApi(configuration);

function TriviaGenPage() {
    const [numberOfQuestions, setNumberOfQuestions] = useState('');
    const [category, setCategory] = useState('');



    const handleSubmit = async (event) => {
        event.preventDefault();

        // api call
        try {
            const completion = await openai.createCompletion({
                model: "gpt-3.5-turbo",
                prompt: 'Generate ${numberOfQuestions} trivia questions about ${category}.',
                // adjust and use token limit if necessary
                // max_tokens: 200
            });

            console.log(completion.data.choices[0].text);
        } catch (error) {
            console.error('Err')
        }

        //insert api call logic here

    };


    return (
        <div>
            <h1>
                Trivia Generator
            </h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label for="triviaTitle">Number of Questions:</label>
                    <input 
                        type="number"
                        value={numberOfQuestions}
                        onChange={e => setNumberOfQuestions(Math.min(10, Math.max(1, parseInt(e.target.value, 10))))}
                        className="form-control" 
                        id="triviaTitle" 
                        placeholder="Number of Questions" 
                    />
                </div>
                <div className="form-group">
                    <label for="triviaCategory">Category:</label>
                    <input 
                        type="text"
                        value={category}
                        onChange={e => setCategory(e.target.value)}
                        className="form-control" 
                        id="triviaCategory" 
                        placeholder="Category" 
                    />
                </div>
                <div className="form-group">
                    <label for="triviaAnswer">placeholder</label>
                    <input type="text" className="form-control" id="triviaAnswer" placeholder="Place holder" />
                </div>
                <button type="submit" className="btn btn-primary">Generate</button>
            </form>

        </div>
    );

}
export default TriviaGenPage;
